-- Topics Data\nINSERT INTO topics (title, category, status, lessons_count, has_assessment, difficulty, description, image, created_at, updated_at) VALUES\n('General Info on Blockchain Tech', 'Basics', 'Published', 3, true, 'Beginner', 'Learn the fundamentals of blockchain technology, including how it works, its key features like decentralization and immutability, and explore various real-world applications across different industries.', 'https://images.unsplash.com/photo-1639762681485-074b7f938ba0?w=400&h=225&fit=crop&crop=center', NOW(), NOW()),\n('Getting Started With Crypto', 'Basics', 'Published', 2, true, 'Beginner', 'Introduction to cryptocurrency basics, understanding digital currencies, and learning how to create and secure your first cryptocurrency wallet with best practices.', 'https://images.unsplash.com/photo-1621761191319-c6fb62004040?w=400&h=225&fit=crop&crop=center', NOW(), NOW()),\n('Using MetaMask', 'Wallets', 'Published', 2, true, 'Beginner', 'Complete guide to installing, setting up, and using MetaMask browser extension. Learn about network switching, token management, and advanced security features.', 'https://images.unsplash.com/photo-1563013544-824ae1b704d3?w=400&h=225&fit=crop&crop=center', NOW(), NOW()),\n('Decentralised Finance (DeFi)', 'DeFi', 'Published', 2, true, 'Intermediate', 'Explore decentralized finance protocols, yield farming strategies, liquidity pools, and learn advanced techniques for maximizing DeFi yields while managing risks.', 'https://images.unsplash.com/photo-1559526324-4b87b5e36e44?w=400&h=225&fit=crop&crop=center', NOW(), NOW()),\n('Non-Fungible Tokens (NFTs)', 'NFTs', 'Published', 1, false, 'Beginner', 'Understanding non-fungible tokens (NFTs), their technology, use cases, marketplaces, and applications in digital art, gaming, and various other industries.', 'https://images.unsplash.com/photo-1640161704729-cbe966a08476?w=400&h=225&fit=crop&crop=center', NOW(), NOW()),\n('Smart Contracts', 'Advanced', 'Published', 2, true, 'Advanced', 'Deep dive into smart contracts, their development, deployment, and real-world applications in various blockchain ecosystems.', 'https://images.unsplash.com/photo-1518186285589-2f7649de83e0?w=400&h=225&fit=crop&crop=center', NOW(), NOW());